$(document).on('turbolinks:load', function(){
  $(".my-rating-9").starRating({
    initialRating: 0,
    starSize: 35,
    useFullStars: true,
    disableAfterRate: true,
    callback: function(currentRating, $el){
      film_id = $('#film_id').val()
      user_id = $('#user_id').val()
      $.ajax({
        url: "/ratings",
        type: "POST",
        data: {star : currentRating,
               film_id: film_id, user_id: user_id}
    });
   }
  });
  $(".my-rating-8").starRating({
    starSize: 35,
    readOnly: true
  });
});
